(649, 10, '_why_', 'field_562d25d7279b9'),
(650, 10, 'description', '<p style="text-align: justify;"><span style="font-weight: 400;">Los hilos y cables Deltex® con memoria de tensión presentan la característica de mantener constante su tensión inicial. Su composición a base de poliéster monofilamento con polvo de fibra de carbón asegura su resistencia en condiciones climáticas extremas. Superan los cables de acero con ventajas en diferentes aplicaciones:</span></p>\r\n\r\n<ul>\r\n	<li style="text-align: justify;"><span style="font-weight: 400;">Memoria de tensión única:\r\n</span><span style="font-weight: 400;">Deltex® puede recibir fuertes vibraciones de viento y regresará a su tensión original (no necesita operación de mantenimiento de re-tensión).</span></li>\r\n	<li style="text-align: justify;"><span style="font-weight: 400;">Resistencia a la ruptura muy elevada:\r\n</span><span style="font-weight: 400;">65 kgF/mm</span><span style="font-weight: 400;">2</span><span style="font-weight: 400;"> (contra 15 kgF/mm</span><span style="font-weight: 400;">2</span><span style="font-weight: 400;"> con el acero clasico) y ruptura al 12% de su tensión.</span></li>\r\n	<li style="text-align: justify;"><span style="font-weight: 400;">Resistente a fuertes condiciones atmosféricas:\r\n</span><span style="font-weight: 400;">Guarda sus características bajo el agua (ej.: lluvias ácidas), el sol (no se oxida y no es sujeto a degradaciones UV) y fuertes temperaturas (-40˚C a +70˚C). Con el calor, Deltex® no se afloja, es termo-retractable.</span></li>\r\n	<li style="text-align: justify;"><span style="font-weight: 400;">Duración de vida excepcional:\r\n</span><span style="font-weight: 400;">Superior a 40 años (pruebas en laboratorios con UV y tensión).</span></li>\r\n	<li style="text-align: justify;"><span style="font-weight: 400;">Deltex® es flexible y con bajo peso:\r\n</span><span style="font-weight: 400;">5 veces menos pesado que el acero, se facilita la manipulación, el montaje y mejora la resistencia (densidad de 1,40kg/dm</span><span style="font-weight: 400;">3</span><span style="font-weight: 400;">).</span></li>\r\n</ul>'),
(651, 10, '_description', 'field_562d2607279ba'),
(652, 10, 'for_whom__', '¿Para quién?'),
(653, 10, '_for_whom__', 'field_562d2626279bb'),
(654, 10, 'whom_desctiption', '<p style="text-align: justify;"><span style="font-weight: 400;">Las características de Deltex® como hilos y cables de tensión, permiten un excelente comportamiento en diferentes aplicaciones agrícolas:</span></p>\r\n\r\n<ul style="text-align: justify;">\r\n	<li style="font-weight: 400;"><span style="font-weight: 400;">Cobertura de cultivos:</span></li>\r\n</ul>\r\n<p style="text-align: justify;"><span style="font-weight: 400;">Invernaderos, viveros, soporte para sarán o malla anti-afida (casa sombra y casa malla), pantalla térmica, techos de protección para cultivos, malla anti-granizo, cobertura de reservorio...</span></p>\r\n\r\n<ul style="text-align: justify;">\r\n	<li style="font-weight: 400;"><span style="font-weight: 400;">Cultivos elevados: </span></li>\r\n	<li style="font-weight: 400;"><span style="font-weight: 400;">Excelentes soportes de potes, bandejas, bolsa de fibra o substrato, para la producción de corte de flores, plantas ornamentales, hortalizas. </span></li>\r\n	<li style="font-weight: 400;"><span style="font-weight: 400;">Suspensión de cultivos:</span></li>\r\n</ul>\r\n<p style="text-align: justify;"><span style="font-weight: 400;">A través su memoria de tensión permanente, Deltex® es un solución única para colgar o guiar cultivos como plantas de tomates y chiles, o otros vegetales, bananos, plantas ornamentales, pero también, como hilo de guía para viñas o estructuras tipo barbacoa para plantas enredaderas. </span></p>'),
(655, 10, '_whom_desctiption', 'field_562d2651279bd'),
